#include <iostream>
#include <vector>

using namespace std;

int maxAbsoluteSum(vector<int> nums) {
    int min = 0, max = 0;
    int currSum = 0;
    for (int i = 0; i < nums.size(); i++) {
        currSum = currSum + nums[i];
        min = min > currSum?currSum:min;
        max = max < currSum?currSum:max;
    }
    cout << "min = " << min << "\t";
    cout << "max = " << max << endl;
    return max - min;
}

